#include <fmt/core.h>
#include <cmath>
#include <iostream>
#include <string>
#include <cassert>

int main() {
    double nan = std::numeric_limits<double>::quiet_NaN();
    double inf = std::numeric_limits<double>::infinity();

    std::string s_nan = fmt::format("{:+06}", nan);
    std::cout << "NAN: '" << s_nan << "'" << std::endl;

    std::string s_inf = fmt::format("{:+06}", inf);
    std::cout << "INF: '" << s_inf << "'" << std::endl;

    // Expected behavior according to issue description (zero flag ignored for nan/inf)
    // So it should be space padded.
    // "+nan" is 4 chars. Width 6. So 2 spaces.
    // Default alignment for numbers is right? Or is it?
    // For numbers, alignment is right by default.

    if (s_nan == "00+nan") {
        std::cout << "Reproduced: Zero padding applied to NAN" << std::endl;
    } else {
        std::cout << "Not Reproduced: " << s_nan << std::endl;
    }

    return 0;
}
EOF
"}

