{"command": "cat > repro_bug.cc <<EOF
#include <fmt/core.h>
#include <cmath>
#include <iostream>
#include <string>
#include <cassert>
#include <limits>

void check(const std::string& result, const std::string& expected, const std::string& desc) {
    if (result != expected) {
        std::cerr << "FAIL: " << desc << "\n"
                  << "  Expected: '" << expected << "'\n"
                  << "  Actual:   '" << result << "'\n";
    } else {
        std::cout << "PASS: " << desc << "\n";
    }
}

int main() {
    double nan = std::numeric_limits<double>::quiet_NaN();
    double inf = std::numeric_limits<double>::infinity();
    double neg_inf = -std::numeric_limits<double>::infinity();

    // Note: -nan is tricky because quiet_NaN() might not have sign bit set or preserved.
    // We can try to construct a negative nan.
    double neg_nan = -nan;

    // NaN tests
    check(fmt::format("{:+06}", nan), "  +nan", "{:+06} nan");
    check(fmt::format("{:<+06}", nan), "+nan  ", "{:<+06} nan");
    check(fmt::format("{:^+06}", nan), " +nan ", "{:^+06} nan");

    // Inf tests
    check(fmt::format("{:+06}", inf), "  +inf", "{:+06} inf");
    check(fmt::format("{:<+06}", inf), "+inf  ", "{:<+06} inf");
    check(fmt::format("{:^+06}", inf), " +inf ", "{:^+06} inf");

    // Negative Inf tests
    check(fmt::format("{:+06}", neg_inf), "  -inf", "{:+06} -inf");

    // Negative NaN tests (platform dependent sign bit, but let's try)
    // If sign bit is preserved, it should be -nan.
    // fmtlib usually handles sign of nan if signbit is set.
    if (std::signbit(neg_nan)) {
         check(fmt::format("{:+06}", neg_nan), "  -nan", "{:+06} -nan");
    } else {
         std::cout << "Skipping negative NaN test as sign bit not preserved/detected\n";
    }

    return 0;
}
EOF
"}

